import React from 'react';
import { useState } from 'react';
import './todo.css';

const Todo = () => {
  const categories = ["Urgent", "Work", "Personal"];
  const [todo, setTodo] = useState([]);
  const [task, setTask] = useState("");
  const [edit, setEdit] = useState("");
  const [category, setCategory] = useState("");

  // Separate arrays for each category
  const [urgentTasks, setUrgentTasks] = useState([]);
  const [workTasks, setWorkTasks] = useState([]);
  const [personalTasks, setPersonalTasks] = useState([]);

  function handleEdit(item) {
    setTask(item);
    setEdit(item);

    if (urgentTasks.includes(item)) {
      setCategory("Urgent");
    } else if (workTasks.includes(item)) {
      setCategory("Work");
    } else {
      setCategory("Personal");
    }
  }

  function handleAdd() {
    if (task.length === 0) {
      alert("Please enter a task");
      return;
    }

    if (category === "Urgent") {
      setUrgentTasks([...urgentTasks, task]);
    } else if (category === "Work") {
      setWorkTasks([...workTasks, task]);
    } else {
      setPersonalTasks([...personalTasks, task]);
    }

    setTask("");
    setCategory("");
  }

  function handleDelete(task) {
    if (urgentTasks.includes(task)) {
      setUrgentTasks(urgentTasks.filter((item) => item !== task));
    } else if (workTasks.includes(task)) {
      setWorkTasks(workTasks.filter((item) => item !== task));
    } else {
      setPersonalTasks(personalTasks.filter((item) => item !== task));
    }
  }

  return (
    <div className="todo-container">
      <div className="todo-input-container">
        <input
          type="text"
          placeholder="Enter your task"
          onChange={(e) => setTask(e.target.value)}
          className="task-input"
          value={task}
        />
      </div>
      <select
        value={category}
        onChange={(e) => setCategory(e.target.value)}
        className="category-select"
      >
        <option value="">Select Category</option>
        {categories.map((cat) => (
          <option key={cat} value={cat}>
            {cat}
          </option>
        ))}
      </select>
      <button className="add-task-button" onClick={handleAdd}>
        Add task
      </button>
      <div className="tasks-columns">
        {/* Urgent Tasks */}
        <div className="task-column">
          <h3 className="column-title">Urgent</h3>
          <ul className="todo-list">
            {urgentTasks.map((item) => {
              if (edit === item) {
                return (
                  <li key={item} className="editing">
                    <input
                      type="text"
                      value={task}
                      onChange={(e) => setTask(e.target.value)}
                      className="edit-input"
                    />
                    <button
                      className="save-button"
                      onClick={() => {
                        setEdit("");
                        const updatedUrgent = urgentTasks.map((t) =>
                          t === item ? task : t
                        );
                        setUrgentTasks(updatedUrgent);
                        setTask("");
                        setCategory("");
                      }}
                    >
                      Save
                    </button>
                  </li>
                );
              }
              return (
                <li key={item} className="todo-item urgent">
                  <div className="todo-content">
                    <span className="task-text">{item.toString()}</span>
                    <div className="button-group">
                      <button
                        className="delete-button"
                        onClick={() => handleDelete(item)}
                      >
                        Delete
                      </button>
                      <button
                        className="edit-button"
                        onClick={() => handleEdit(item)}
                      >
                        Edit
                      </button>
                    </div>
                    <div className="category-display">
                      <span className="category-label">Urgent</span>
                    </div>
                  </div>
                </li>
              );
            })}
          </ul>
        </div>

        {/* Work Tasks */}
        <div className="task-column">
          <h3 className="column-title">Work</h3>
          <ul className="todo-list">
            {workTasks.map((item) => {
              if (edit === item) {
                return (
                  <li key={item} className="editing">
                    <input
                      type="text"
                      value={task}
                      onChange={(e) => setTask(e.target.value)}
                      className="edit-input"
                    />
                    <button
                      className="save-button"
                      onClick={() => {
                        setEdit("");
                        const updatedWork = workTasks.map((t) =>
                          t === item ? task : t
                        );
                        setWorkTasks(updatedWork);
                        setTask("");
                        setCategory("");
                      }}
                    >
                      Save
                    </button>
                  </li>
                );
              }
              return (
                <li key={item} className="todo-item work">
                  <div className="todo-content">
                    <span className="task-text">{item.toString()}</span>
                    <div className="button-group">
                      <button
                        className="delete-button"
                        onClick={() => handleDelete(item)}
                      >
                        Delete
                      </button>
                      <button
                        className="edit-button"
                        onClick={() => handleEdit(item)}
                      >
                        Edit
                      </button>
                    </div>
                    <div className="category-display">
                      <span className="category-label">Work</span>
                    </div>
                  </div>
                </li>
              );
            })}
          </ul>
        </div>

        {/* Personal Tasks */}
        <div className="task-column">
          <h3 className="column-title">Personal</h3>
          <ul className="todo-list">
            {personalTasks.map((item) => {
              if (edit === item) {
                return (
                  <li key={item} className="editing">
                    <input
                      type="text"
                      value={task}
                      onChange={(e) => setTask(e.target.value)}
                      className="edit-input"
                    />
                    <button
                      className="save-button"
                      onClick={() => {
                        setEdit("");
                        const updatedPersonal = personalTasks.map((t) =>
                          t === item ? task : t
                        );
                        setPersonalTasks(updatedPersonal);
                        setTask("");
                        setCategory("");
                      }}
                    >
                      Save
                    </button>
                  </li>
                );
              }
              return (
                <li key={item} className="todo-item personal">
                  <div className="todo-content">
                    <span className="task-text">{item.toString()}</span>
                    <div className="button-group">
                      <button
                        className="delete-button"
                        onClick={() => handleDelete(item)}
                      >
                        Delete
                      </button>
                      <button
                        className="edit-button"
                        onClick={() => handleEdit(item)}
                      >
                        Edit
                      </button>
                    </div>
                    <div className="category-display">
                      <span className="category-label">Personal</span>
                    </div>
                  </div>
                </li>
              );
            })}
          </ul>
        </div>
      </div>
    </div>
  );
};

export default Todo;
