import React from 'react';
import { useState } from 'react';
import './todo.css';

const Todo = () => {
  const categories = ["Urgent", "Work", "Personal"];
  const [todo, setTodo] = useState([]);
  const [task, setTask] = useState("");
  const [edit, setEdit] = useState("");
  const [category, setCategory] = useState("");

  function handleEdit(item) {
    setTask(item);
    setEdit(item);
  }

  function handleAdd() {
    if (task.length === 0) {
      alert("Please enter a task");
      return;
    }
    setTodo([...todo, task]);
  }

  function handleDelete(task) {
    setTodo(todo.filter((item) => item !== task));
  }

  return (
    <div className="todo-container">
      <div className="todo-input-container">
        <input
          type="text"
          placeholder="Enter your task"
          onChange={(e) => setTask(e.target.value)}
          className="task-input"
        />
      </div>
      <select
        value={category}
        onChange={(e) => setCategory(e.target.value)}
        className="category-select"
      >
        <option value="">Select Category</option>
        {categories.map((cat) => (
          <option key={cat} value={cat}>
            {cat}
          </option>
        ))}
      </select>
      <button className="add-task-button" onClick={handleAdd}>
        Add task
      </button>
      <ul className="todo-list">
        {todo.map((item) => {
          if (edit === item) {
            return (
              <li key={item} className="editing">
                <input
                  type="text"
                  value={task}
                  onChange={(e) => setTask(e.target.value)}
                  className="edit-input"
                />
                <button
                  className="save-button"
                  onClick={() => {
                    setEdit("");
                    const index = todo.indexOf(item);
                    todo[index] = task;
                    setTodo([...todo]);
                    setTask("");
                  }}
                >
                  Save
                </button>
              </li>
            );
          }
          return (
            <li key={item} className="todo-item">
              <div className="todo-content">
                <span className="task-text">{item.toString()}</span>
                <div className="button-group">
                  <button
                    className="delete-button"
                    onClick={() => handleDelete(item)}
                  >
                    Delete
                  </button>
                  <button
                    className="edit-button"
                    onClick={() => handleEdit(item)}
                  >
                    Edit
                  </button>
                </div>
                <div className="category-display">
                  <span className="category-label">{category}</span>
                </div>
              </div>
            </li>
          );
        })}
      </ul>
    </div>
  );
};

export default Todo;
