import React from 'react'
import {useState, useEffect} from 'react'

const Todo = () => {
const [todo,setTodo]=useState([]);
const [task,setTask]=useState("");
const [edit,setEdit]=useState("");
function handleEdit(item){
    setTask(item);
    setEdit(item);
}
function handleAdd(){
  
    if(task.length === 0){
        alert("Please enter a task")
        return;
    }
    setTodo([...todo,task]);//adding the task
}
function handleDelete(task){
    setTodo(todo.filter((item)=>item !== task));
}



return (
    <div>
      <input type="text" placeholder="Enter your task" onChange={(e)=>setTask(e.target.value)}/>
      <button onClick={handleAdd}>Add task</button>
      <ul>
        {todo.map((item)=>{
              if (edit === item) {
                // If this task is being edited, show input field
                return (
                  <li key={item}>
                    <input
                      type="text"
                      value={task} // Bind the current task value to the input field
                      onChange={(e) => setTask(e.target.value)} // Update task as user types
                    />
                    <button onClick={() => function(){
                        setEdit("")
                        
                        }
                        }>Save</button>
                  </li>
                );
              }
            return <li key={item}>
                <div>
                    <span>{item.toString()}</span>
                    <button onClick={()=>handleDelete(item)}>Delete</button>
                    <button onClick={()=>handleEdit(item)}>Edit</button>
                </div>
            </li>
            
        })}
      </ul>
      </div>
  )
}

export default Todo;
